import { api } from './api';
import AsyncStorage from "@react-native-async-storage/async-storage";

import { APP_STORAGE_TOKEN } from "../constants/user.constants";
api.interceptors.request.use((config) => {
    const token = AsyncStorage.getItem(APP_STORAGE_TOKEN)
    config.headers.Authorization = `Bearer ${token}`
    return config
})
export const createUser = (user_data) => {

    return api.post('api/user/create/', user_data).then(res => {
        return { errors: false, data: res.data };
    }).catch(e => {
        if (e.response) {
            return { errors: true, data: e.response.data }
        }
        return { errors: false, data: e }

    })
}

export const createToken = (data) => {
    return api.post('api/user/token/', data).then(response => {
        if (response.status == 200) {
            return response.data
        }
    }).catch(e => {
        if (e.response) {
            console.log(e.response)
        }
    })
}

export const getMe = async () => {
    return await api.get('api/user/me')
}

export const updateToken = (token) => {
    api.defaults.headers.common["Authorization"] = `Bearer ${token}`
}

export const defineInterceptor = () => {

    api.interceptors.request.use(request => {
        const token = AsyncStorage.getItem(APP_STORAGE_TOKEN)
        if (token) {
            request.headers.common.Authorization = `Bearer ${token}`;
        }
    });
}
